import { Component, Input, OnInit } from '@angular/core';
import { products, Product } from '../products';
import { ActivatedRoute } from '@angular/router';
import { CartService } from '../cart.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})

export class ProductComponent implements OnInit{

  products = [...products];
  @Input() product!:Product;

  constructor(
    private route: ActivatedRoute,
    private cartService: CartService
  ) { }
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  addToCart(product: Product) {
    this.cartService.addToCart(product);
    window.alert('Your product has been added to the cart!');
  }

  share() {
    window.alert('El producto ha sido compartido');
  }

  onNotify() {
    window.alert('Se le notificará cuando el producto salga a la venta');
  }

  display: boolean = false;

  showDialog() {
      this.display = true;
  }


}

<h2>Products</h2>



  <h3>
    <a [title]="product.name + ' details'">
      {{ product.name }}
    </a>
  </h3>

  <p *ngIf="product.description">
    Description: {{ product.description }}
  </p>

  <p-dialog header="Title" [(visible)]="display">
    <img src="../../assets/phones/{{product.images}}" width="400px" height="400px" alt="Sin imagen">  
</p-dialog>

<button  class="btn btn-light"  type="button" (click)="showDialog()" icon="pi pi-info-circle" label="Show">Details</button>

<button class="btn btn-light" type="button" (click)="share()">Share</button>

  <app-product-alerts
  [product]="product" 
  (notify)="onNotify()">
</app-product-alerts>
